import React, { useState } from 'react';
import CameraCapture from './components/CameraCapture';

function App() {
  const [prediction, setPrediction] = useState(null);

  const handleImageCapture = async (imageBlob) => {
    const formData = new FormData();
    formData.append('file', imageBlob, 'fish.jpg');

    const res = await fetch('http://127.0.0.1:8000/predict', {
      method: 'POST',
      body: formData
    });

    const data = await res.json();
    setPrediction(data);
  };

  return (
    <div className="app-container">
      <h1>Fish ID App</h1>
      <CameraCapture onImageCapture={handleImageCapture} />
      {prediction && (
        <div className="result-box">
          <h2>Prediction Result:</h2>
          <p><strong>Fish Type:</strong> {prediction.fish_type}</p>
          <p><strong>Description:</strong> {prediction.description}</p>
          <p><strong>Habitat:</strong> {prediction.habitat}</p>
          <p><strong>Edibility:</strong> {prediction.edibility}</p>
        </div>
      )}
    </div>
  );
}

export default App;
