import React, { useState } from 'react';
import CameraCapture from './components/CameraCapture';
import PredictionCard from './components/PredictionCard';

function App() {
  const [history, setHistory] = useState([]);
  const [loading, setLoading] = useState(false);

  const handleImageCapture = async (imageBlob) => {
    setLoading(true);
    const formData = new FormData();
    formData.append('file', imageBlob, 'fish.jpg');

    try {
      const res = await fetch('http://127.0.0.1:8000/predict', {
        method: 'POST',
        body: formData
      });

      const data = await res.json();
      setHistory(prev => [{ image: URL.createObjectURL(imageBlob), result: data }, ...prev]);
    } catch (err) {
      console.error("Error:", err);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="app-container">
      <h1>Fish ID App</h1>
      <CameraCapture onImageCapture={handleImageCapture} />
      {loading && <div className="spinner">Analyzing...</div>}

      <div className="history-container">
        {history.map((item, index) => (
          <PredictionCard key={index} image={item.image} result={item.result} />
        ))}
      </div>
    </div>
  );
}

export default App;
